(43, 42, 12, 2, 0, '65.190.15.53', 1306031037, 0, 1, 1, 1, 1, '', 'RSCE Doric NPC handler', '[code:11jbac6b]package server&#46;npchandler;\n\nimport server&#46;DelayedEventHandler;\nimport server&#46;Server;\nimport server&#46;event&#46;ShortEvent;\nimport server&#46;model&#46;ChatMessage;\nimport server&#46;model&#46;InvItem;\nimport server&#46;model&#46;Inventory;\nimport server&#46;model&#46;MenuHandler;\nimport server&#46;model&#46;Npc;\nimport server&#46;model&#46;Player;\nimport server&#46;model&#46;World;\nimport server&#46;packetbuilder&#46;client&#46;MiscPacketBuilder;\n\npublic class Doric implements NpcHandler {\n   private static byte QUEST_ID = 1;\n   private static int CLAY_ID = 149;\n   private static int COPPER_ID = 150;\n   private static int IRON_ID = 151;\n  \n   public void handleNpc(Npc npc, Player player) throws Exception {\n      switch(player&#46;getQuestCompletionStage(QUEST_ID)) {\n         case 0&#58;\n            player&#46;setBusy(true);\n            player&#46;informOfNpcMessage(new ChatMessage(npc, &quot;Do you have my ores?&quot;, player));\n            Server&#46;requestWorldAccess()&#46;getDelayedEventHandler()&#46;add(new ShortEvent(player, npc) {\n               public void action() {\n                  String&#91;&#93; options = { &quot;Yes, here they are&#46;&quot;, &quot;No, not yet&#46;&quot; };\n                  owner&#46;setMenuHandler(new MenuHandler(options) {\n                     public void handleReply(int option, String reply) {\n                        owner&#46;setBusy(false);\n                        if(owner&#46;isBusy()) {\n                           return;\n                        }\n                        owner&#46;informOfChatMessage(new ChatMessage(owner, reply, npc));\n                        owner&#46;setBusy(true);\n                        Server&#46;requestWorldAccess()&#46;getDelayedEventHandler()&#46;add(new ShortEvent(owner, reply, option) {\n                           public void action() {\n                              owner&#46;informOfNpcMessage(new ChatMessage(owner, reply, npc));\n                              if(option == 0) {\n                                 Server&#46;requestWorldAccess()&#46;getDelayedEventHandler()&#46;add(new ShortEvent(owner) {\n                                    public void action() {\n                                       if((owner&#46;getInventory()&#46;countId(CLAY_ID) &lt; 6) || (owner&#46;getInventory()&#46;countId(COPPER_ID) &lt; 4) || (owner&#46;getInventory()&#46;countId(IRON_ID) &lt; 2)) {\n                                          owner&#46;informOfNpcMessage(new ChatMessage(npc, &quot;You don''t have all of the ores, return when you do&#46;&quot;, owner));\n                                          owner&#46;setBusy(false);\n                                          npc&#46;unblock();\n                                       } else {\n                                          owner&#46;informOfNpcMessage(new ChatMessage(npc, &quot;Thanks, &quot; + owner&#46;getUsername() + &quot; feel free to use my anvil&#46;&quot;, owner));\n                                          owner&#46;getInventory()&#46;remove(CLAY_ID, 1);\n                                          owner&#46;getInventory()&#46;remove(CLAY_ID, 1);\n                                          owner&#46;getInventory()&#46;remove(CLAY_ID, 1);\n                                          owner&#46;getInventory()&#46;remove(CLAY_ID, 1);\n                                          owner&#46;getInventory()&#46;remove(CLAY_ID, 1);\n                                          owner&#46;getInventory()&#46;remove(CLAY_ID, 1);\n                                          owner&#46;getInventory()&#46;remove(COPPER_ID, 1);\n                                          owner&#46;getInventory()&#46;remove(COPPER_ID, 1);\n                                          owner&#46;getInventory()&#46;remove(COPPER_ID, 1);\n                                          owner&#46;getInventory()&#46;remove(COPPER_ID, 1);\n                                          owner&#46;getInventory()&#46;remove(IRON_ID, 1);\n                                          owner&#46;getInventory()&#46;remove(IRON_ID, 1);\n                                          owner&#46;getActionSender()&#46;sendInventory();\n                                          owner&#46;incQuestCompletionStage(QUEST_ID);\n                                          completeQuest(owner);\n                                          owner&#46;setBusy(false);\n                                          npc&#46;unblock();\n                                       }\n                                    }\n                                 });\n                              } else {\n                                 owenr&#46;setBusy(false);\n                                 npc&#46;unblock();\n                              }\n                           }\n                        });\n                     }\n                  });\n                  owner&#46;getActionSender()&#46;sendMenu(options);\n               }\n            });\n            npc&#46;blockedBy(player);\n            break;\n         case 1&#58;\n            player&#46;informOfNpcMessage(new ChatMessage(npc, &quot;Hello again, &quot; + player&#46;getUsername() + &quot;&#46;&quot;, player));\n            break;\n         default&#58;\n            player&#46;setBusy(true);\n            player&#46;informOfNpcMessage(new ChatMessage(npc, &quot;Hello, want to earn the right to use my anvil?&quot;, player));\n            Server&#46;requestWorldAccess()&#46;getDelayedEventHandler()&#46;add(new ShortEvent(player, npc) {\n               public void action() {\n                  String&#91;&#93; options = { &quot;Yes, I would like to&#46;&quot;, &quot;Sorry, but I do not have the time&#46;&quot; };\n                  owner&#46;setMenuHandler(new MenuHandler(options) {\n                     public void handleReply(int option, String reply) {\n                        owner&#46;informOfChatMessage(new ChatMessage(owner, reply, npc));\n                        owner&#46;setBusy(false);\n                        if(owner&#46;isBusy()) {\n                           return;\n                        }\n                        owner&#46;setBusy(true);\n                        if(option == 0) {\n                           Server&#46;requestWorldAccess()&#46;getDelayedEventHandler()&#46;add(new ShortEvent(owner) {\n                              public void action() {\n                                 Server&#46;requestWorldAccess()&#46;getDelayedEventHandler()&#46;add(new ShortEvent(owner) {\n                                    public void action() {\n                                       owner&#46;addQuest(QUEST_ID);\n                                       owner&#46;informOfNpcMessage(new ChatMessage(npc, &quot;I''ll need 6 clay, 4 copper, and 2 iron ores&#46;&quot;, owner));\n                                       owner&#46;setBusy(false);\n                                       npc&#46;unblock();\n                                    }\n                                 });\n                              }\n                           });\n                        } else {\n                           owner&#46;setBusy(false);\n                           npc&#46;unblock();\n                        }\n                     }\n                  });\n                  owner&#46;getActionSender()&#46;sendMenu(options);\n               }\n            });\n            npc&#46;blockedBy(player);\n         }\n      }\n      \n      private void completeQuest(Player player) {\n         player&#46;getInventory()&#46;add(new InvItem(10, 180));\n         player&#46;getActionSender()&#46;sendInventory();\n         player&#46;incQuestExp(13, 180);\n         player&#46;getActionSender()&#46;sendStat(13);\n         player&#46;getActionSender()&#46;sendMessage(&quot;@gre@You have completed Doric''s Quest!&quot;);\n      }\n   }\n[/code:11jbac6b]', '4f8e92d7901b2fbaed1e46f8dd165bd0', 0, 'AIA=', '11jbac6b', 1, 0, '', 0, 0, 0, 1, 0, '', 0),
(44, 43, 12, 2, 0, '65.190.15.53', 1306031128, 0, 1, 1, 1, 1, '', 'Outside wilderness, wilderness', 'Define the inBounds of your ''new wild'' area in your Mudclient class:\n\nSomething like:\n\n[code:2lfagz5n]\n   public boolean newWildernessArea() {\n      int x = sectionX + areaX;\n      int y = sectionY + areaY;\n      return (( x &lt;= maxX) &amp;&amp; (x &gt;= minX) &amp;&amp; (y &lt;= maxY) &amp;&amp; (y &gt;= minY));\n   }[/code:2lfagz5n]Change the min and max X &amp; Y to your inBounds.... Then find the attack method:\n\n[code:2lfagz5n]\nif (i &gt; 0 &amp;&amp; (playerArray&#91;i2&#93;&#46;currentY - 64) / magicLoc + wildY + areaY &lt; 2203) {[/code:2lfagz5n]Change that to:\n\n[code:2lfagz5n]\nif (i &gt; 0 &amp;&amp; (playerArray&#91;i2&#93;&#46;currentY - 64) / magicLoc + wildY + areaY &lt; 2203 || newWildernessArea()) {[/code:2lfagz5n]That will bring up the ''Attack'' option in your specific inBounds...\n\nYou will also want to take a look at this method, which will draw the wilderness icon...\n\n[code:2lfagz5n]\n      if (!notInWilderness) {\n            int j6 = 2203 - (sectionY + wildY + areaY);\n            if (sectionX + wildX + areaX &gt;= 2640)\n                j6 = -50;\n            if (j6 &gt; 0) {\n                int k8 = 1 + j6 / 6;\n                gameGraphics&#46;drawPicture(453, windowHeight - 56, SPRITE_MEDIA_START + 13);\n                gameGraphics&#46;drawText(&quot;Wilderness&quot;, 465, windowHeight - 20, 1, 0xffff00);\n            gameGraphics&#46;drawText(&quot;Level&#58; &quot; + k8, 465, windowHeight - 7, 1, 0xffff00);\n                if (wildernessType == 0)\n                    wildernessType = 2;\n            }\n            if (wildernessType == 0 &amp;&amp; j6 &gt; -10 &amp;&amp; j6 &lt;= 0)\n                wildernessType = 1;\n        }[/code:2lfagz5n]There may be more options client side... Try taking a look at the wildX and wildY definitions...\n\nAlso, you will need to define your inBounds server side... More than likely in your Point class.\n\n\nServer side code:\n\nLocate the public boolean checkAttack() method and add according to location name previously stated inbounds in the point class:\n\n[code:2lfagz5n]if(getLocation()&#46;inModRoom() &amp;&amp; victim&#46;getLocation()&#46;inModRoom()){\n                return true;\n            }[/code:2lfagz5n]', '3375fc8f9037bd5de6644c6e982d15bf', 0, 'AIA=', '2lfagz5n', 1, 0, '', 0, 0, 0, 1, 0, '', 0),
(45, 44, 12, 2, 0, '65.190.15.53', 1306031153, 0, 1, 1, 1, 1, '', 'Log player logins', 'This is something quite simple, but could be quite useful. What this will do is basically log information from the Player who is logging in. The information is then stored into a text file. This system will log the players IP, username, time of login, etc.\n\nHere is the output example of how it will be displayed in the text file:\n\nYong Min logged in from 127.0.0.1 on the 27-06-2010 at 10:50:23\n\nNow go into your ''logs'' folder, server side, and create a new text file called ''logins.txt''.\n\nNow open your Player class, and find:\n\n[code:2rmlz001]\nint timeTillShutdown = world&#46;getServer()&#46;timeTillShutdown();[/code:2rmlz001]Above that, add:\n\n[code:2rmlz001]\nworld&#46;getServer()&#46;addLogin();[/code:2rmlz001]Now in your Server class, find:\n\n[code:2rmlz001]\n   /**\n    * Shutdown the server in 60 seconds\n    */\n   public boolean shutdownForUpdate() {\n      if(updateEvent != null) {\n         return false;\n      }\n      updateEvent = new SingleEvent(null, 65000) {\n             public void action() {\n                kill();\n             }\n          };\n      world&#46;getDelayedEventHandler()&#46;add(updateEvent);\n      return true;\n   }[/code:2rmlz001]Under that, add:\n\n[code:2rmlz001]\n   /**\n    * Login Logger @author Yong Min\n    */\n   public static final String DATE_FORMAT_NOW = &quot;dd-MM-yyyy&quot;;\n   public static final String TIME_FORMAT_NOW = &quot;HH&#58;mm&#58;ss&quot;;\n\n   Calendar calendar = Calendar&#46;getInstance();\n   SimpleDateFormat date = new SimpleDateFormat(DATE_FORMAT_NOW);\n   SimpleDateFormat time = new SimpleDateFormat(TIME_FORMAT_NOW);\n\n   public void addLogin() {\n      for(Player p &#58; world&#46;getPlayers()) {\n         try {\n            BufferedWriter login = new BufferedWriter(new FileWriter(&quot;logs/&quot; + &quot;logins&#46;txt&quot;, true));\n            login&#46;write(p&#46;getUsername() + &quot; logged in from &quot; + p&#46;getCurrentIP() + &quot; on the &quot; + date&#46;format(calendar&#46;getTime()) + &quot; at &quot; + time&#46;format(calendar&#46;getTime()));\n            login&#46;newLine();\n            login&#46;flush();\n            login&#46;close();\n            //System&#46;out&#46;println(&quot;The login for &quot; + p&#46;getUsername() + &quot; has been successfully logged&#46;&quot;);\n            }\n            catch (Exception ex) {\n               System&#46;out&#46;println(ex);\n         }\n      }\n   }[/code:2rmlz001]And your imports:\n\n[code:2rmlz001]\nimport java&#46;util&#46;Calendar;\nimport java&#46;text&#46;SimpleDateFormat;\nimport java&#46;io&#46;BufferedWriter;\nimport java&#46;io&#46;FileWriter;[/code:2rmlz001]', '6696cc12e3a15e34612afe1e9fe70ffe', 0, 'AIA=', '2rmlz001', 1, 0, '', 0, 0, 0, 1, 0, '', 0),
