(66, 65, 12, 2, 0, '65.190.15.53', 1306031715, 0, 1, 1, 1, 1, '', 'Tutorial island NPCs BSH format', 'Yong Min: All tutorial island NPCs are wrote. I am now going to be going through them 1 by 1 and making sure everything works smoothly... Once i''ve checked through them, I will add their BSH script to this topic. As I am writing these up for V4, they have a progression check within them too.\n\n476 - Guide.BSH\n\n[code:38red54p]\n/**\n * 476 - Guide @author Yong Min\n */\n\nif(player&#46;getTutorialStatus() == 7) { // Spoke to the guide\n   NpcTalk(&quot;Please proceed through the next door&quot;);\n} else\nif(player&#46;getTutorialStatus() == 0) { // Not started the tutorial island yet\n   NpcTalk(&quot;Welcome to the world of runescape&quot;);\n   NpcTalk(&quot;My job is to help newcomers find their feet here&quot;);\n   PlayerTalk(&quot;Ah good, let''s get started&quot;);\n   NpcTalk(&quot;When speaking to characters such as myself&quot;);\n   NpcTalk(&quot;Sometimes options will appear in the top left corner of the screen&quot;);\n   NpcTalk(&quot;left click on one of them to continue the conversation&quot;);\n   option = PickOption(&quot;So what else can you tell me?&quot;, &quot;What controls do I have?&quot;, &quot;I want to skip tutorial island&quot;);\n   if(option == 1) {\n      NpcTalk(&quot;I suggest you go through the door now&quot;);\n      NpcTalk(&quot;There are several guides and advisors on the island&quot;);\n      NpcTalk(&quot;Speak to them&quot;);\n      NpcTalk(&quot;They will teach you about the various aspects of the game&quot;);\n      player&#46;setTutorialStatus(7);\n      player&#46;getActionSender()&#46;sendTutorialStatus();\n   } else\n   if(option == 2) {\n      NpcTalk(&quot;I suggest you talk to the controls guide through the door&quot;);\n      player&#46;setTutorialStatus(7);\n      player&#46;getActionSender()&#46;sendTutorialStatus();\n   } else\n   if(option == 3) {\n      NpcTalk(&quot;You will now be teleported to the mainland&quot;);\n      NpcTalk(&quot;Good luck&quot;);\n      player&#46;setTutorialStatus(100);\n      player&#46;getActionSender()&#46;sendTutorialStatus();\n      player&#46;teleport(122, 647, true);\n   }\n}[/code:38red54p]\n499 - controls guide.BSH\n\n[code:38red54p]\n/**\n * 499 - controls guide @author Yong Min\n */\nif(player&#46;getTutorialStatus() == 15) { // Spoke to the controls guide\n   NpcTalk(&quot;Please proceed through the next door&quot;);\n} else {\n   NpcTalk(&quot;Hello I''m here to tell you more about the game''s controls&quot;);\n   NpcTalk(&quot;Most of your options and character information&quot;);\n   NpcTalk(&quot;can be accessed by the menus in the top right corner of the screen&quot;);\n   NpcTalk(&quot;moving your mouse over the map icon&quot;);\n   NpcTalk(&quot;which is the second icon from the right&quot;);\n   NpcTalk(&quot;gives you a view of the area you are in&quot;);\n   NpcTalk(&quot;clicking on this map is an affective way of walking around&quot;);\n   NpcTalk(&quot;though if the route is blocked, for example by a closed door&quot;);\n   NpcTalk(&quot;then your character won''t move&quot;);\n   NpcTalk(&quot;Also notice the compass on the map which may be of help to you&quot;);\n   PlayerTalk(&quot;Thank you for your help&quot;);\n   NpcTalk(&quot;Now carry on to speak to the combat instructor&quot;);\n   player&#46;setTutorialStatus(15);\n   player&#46;getActionSender()&#46;sendTutorialStatus();\n}[/code:38red54p]\n474 - Combat instructor.BSH\n\n[code:38red54p]\n/**\n * 474 - Combat instructor @author Yong Min\n */\nplayersExperience = ((player&#46;getExp(0)) + (player&#46;getExp(1)) + (player&#46;getExp(2)));\n\nif((playersExperience == 0) &amp;&amp; (player&#46;getWeaponPowerPoints() &lt;= 1) &amp;&amp; (!HasItem(4, 1)) &amp;&amp; (!HasItem(70, 1)) &amp;&amp; (player&#46;getTutorialStatus() == 15)) {\n   NpcTalk(&quot;Aha a new recruit&quot;);\n   NpcTalk(&quot;I''m here to teach you the basics of fighting&quot;);\n   NpcTalk(&quot;First of all you need weapons&quot;);\n   AddItem(70, 1); // Add bronze long sword to the players inventory\n   AddItem(4, 1); // Add wooden shield to the players inventory\n   SendMessage(&quot;The instructor gives you a sword and a shield&quot;);\n   Wait(2000);\n   NpcTalk(&quot;look after these well&quot;);\n   NpcTalk(&quot;These items will now have appeared in your inventory&quot;);\n   NpcTalk(&quot;You can access them by selecting the bag icon in the menu bar&quot;);\n   NpcTalk(&quot;which can be found in the top right hand corner of the screen&quot;);\n   NpcTalk(&quot;To wield your weapon and shield left click on them within your inventory&quot;);\n   NpcTalk(&quot;their box will go red to show you are wearing them&quot;);\n   SendMessage(&quot;when you have done this speak to the combat instructor again&quot;);\n} else\nif((playersExperience == 0) &amp;&amp; (player&#46;getWeaponPowerPoints() &gt; 1)) { // Player is wielding the sword/shield\n   NpcTalk(&quot;Great job! now you see those rats inside the fense?&quot;);\n   NpcTalk(&quot;Go and kill one then come talk to me&quot;);\n} else\nif((playersExperience &gt; 0) &amp;&amp; (player&#46;getTutorialStatus() == 22)) { // 22% status obtained after killing the rat\n   NpcTalk(&quot;Well done you''re a born fighter&quot;);\n   NpcTalk(&quot;As you kill things&quot;);\n   NpcTalk(&quot;Your combat experience will go up&quot;);\n   NpcTalk(&quot;this experience will slowly cause you to get tougher&quot;);\n   NpcTalk(&quot;eventually you will be able to take on stronger enemies&quot;);\n   NpcTalk(&quot;Such as those found in dungeons&quot;);\n   NpcTalk(&quot;Now continue to the building to the northeast&quot;);\n   player&#46;setTutorialStatus(23);\n   player&#46;getActionSender()&#46;sendTutorialStatus();\n} else\nif(player&#46;getTutorialStatus() == 23) {\n   NpcTalk(&quot;Please proceed through the next door&quot;);\n}[/code:38red54p]\n478 - cooking instructor.BSH\n\n[code:38red54p]\n/**\n * 478 - cooking instructor @author Yong Min\n */\n\nif(player&#46;getTutorialStatus() == 23) { // Not spoke to the cook yet\n   NpcTalk(&quot;Looks like you''ve been fighting&quot;);\n   NpcTalk(&quot;If you get hurt in a fight&quot;);\n   NpcTalk(&quot;you will slowly heal&quot;);\n   NpcTalk(&quot;Eating food will heal you much more quickly&quot;);\n   NpcTalk(&quot;I''m here to show you some simple cooking&quot;);\n   NpcTalk(&quot;First you need something to cook&quot;);\n   AddItem(133, 1); // Add raw chicken to the players inventory\n   SendMessage(&quot;the instructor gives you a piece of meat&quot;);\n   Wait(1000);\n   NpcTalk(&quot;ok cook it on the range&quot;);\n   NpcTalk(&quot;To use an item you are holding&quot;);\n   NpcTalk(&quot;Open your inventory and click on the item you wish to use&quot;);\n   NpcTalk(&quot;Then click on whatever you wish to use it on&quot;);\n   NpcTalk(&quot;In this case use it on the range&quot;);\n   player&#46;setTutorialStatus(25);\n   player&#46;getActionSender()&#46;sendTutorialStatus();\n} else\nif((HasItem(133, 1)) &amp;&amp; (player&#46;getTutorialStatus() == 25)) { // Not used the meat on the range yet\n   NpcTalk(&quot;use the meat on the range then talk to me&quot;);\n} else\nif((HasItem(134, 1)) &amp;&amp; (!HasItem(132, 1))) { // The player has burnt the meat\n   PlayerTalk(&quot;I burnt the meat&quot;);\n   NpcTalk(&quot;Well i''m sure you''ll get the hang of it soon&quot;);\n   NpcTalk(&quot;Let''s try again&quot;);\n   NpcTalk(&quot;Here''s another piece of meat to cook&quot;);\n   AddItem(133, 1); // Add raw chicken to the players inventory\n   SendMessage(&quot;the instructor gives you another piece of meat&quot;);\n} else\nif((HasItem(132, 1)) &amp;&amp; (player&#46;getTutorialStatus() == 31)) { // The player has cooked the meat and spoke to the instructor again\n   NpcTalk(&quot;Proceed through the door, I have taught you enough&quot;);\n} else\nif((HasItem(132, 1)) &amp;&amp; (player&#46;getTutorialStatus() == 25)) {\n   PlayerTalk(&quot;I''ve cooked the meat correctly this time&quot;);\n   NpcTalk(&quot;very well done&quot;);\n   NpcTalk(&quot;Now you can tell whether you need to eat or not&quot;);\n   NpcTalk(&quot;look in your stats menu&quot;);\n   NpcTalk(&quot;Click on the bar graph icon in the menu bar&quot;);\n   NpcTalk(&quot;Your stats are low right now&quot;);\n   NpcTalk(&quot;As you use the various skills, these stats will increase&quot;);\n   NpcTalk(&quot;If you look at your hits you will see 2 numbers&quot;);\n   NpcTalk(&quot;The number on the right is your hits when you are at full health&quot;);\n   NpcTalk(&quot;The number on the left is your current hits&quot;);\n   NpcTalk(&quot;If the number on the left is lower eat some food to be healed&quot;);\n   NpcTalk(&quot;There are many other sorts of food you can cook&quot;);\n   NpcTalk(&quot;As your cooking level increases you will be able to cook even more&quot;);\n   NpcTalk(&quot;some of these dishes are more complicated to prepare&quot;);\n   NpcTalk(&quot;If you want to know more about cookery&quot;);\n   NpcTalk(&quot;You could consult the online manual&quot;);\n   NpcTalk(&quot;Now proceed through the next door&quot;);\n   player&#46;setTutorialStatus(31);\n   player&#46;getActionSender()&#46;sendTutorialStatus();\n}[/code:38red54p]\n480 - financial advisor.BSH\n\n[code:38red54p]\n/**\n * 480 - financial advisor @author Yong Min\n */\nif(player&#46;getTutorialStatus() == 37) { // Already spoke to the financial advisor\n   NpcTalk(&quot;Please proceed through the next door&quot;);\n} else {\n   NpcTalk(&quot;Hello there&quot;);\n   NpcTalk(&quot;I''m your designated financial advisor&quot;);\n   PlayerTalk(&quot;That''s good because I don''t have any money at the moment&quot;);\n   PlayerTalk(&quot;How do I get rich?&quot;);\n   NpcTalk(&quot;There are many different ways to make money in runescape&quot;);\n   NpcTalk(&quot;for example certain monsters will drop a bit of loot&quot;);\n   NpcTalk(&quot;To start with killing men and goblins might be a good idea&quot;);\n   NpcTalk(&quot;Some higher level monsters will drop quite a lot of treasure&quot;);\n   NpcTalk(&quot;several of runescapes skills are good money making skills&quot;);\n   NpcTalk(&quot;two of these skills are mining and fishing&quot;);\n   NpcTalk(&quot;there are instructors on the island who will help you with this&quot;);\n   NpcTalk(&quot;using skills and combat to make money is a good plan&quot;);\n   NpcTalk(&quot;because using a skill also slowly increases your level in that skill&quot;);\n   NpcTalk(&quot;A high level in a skill opens up many more opportunities&quot;);\n   NpcTalk(&quot;Some other ways of making money include taking quests and tasks&quot;);\n   NpcTalk(&quot;You can find these by talking to certain game controlled characters&quot;);\n   NpcTalk(&quot;Our quest advisors will tell you about this&quot;);\n   NpcTalk(&quot;Sometimes you will find items lying around&quot;);\n   NpcTalk(&quot;Selling these to the shops makes some money too&quot;);\n   NpcTalk(&quot;Now continue through the next door&quot;);\n   player&#46;setTutorialStatus(37);\n   player&#46;getActionSender()&#46;sendTutorialStatus();\n}[/code:38red54p]\n479 - fishing instructor.BSH\n\n[code:38red54p]\n/**\n * 479 - fishing instructor @author Yong Min\n */\n\nif(HasItem(349, 1)) { // The player has caught a shrimp\n   NpcTalk(&quot;Well done you can now continue with the tutorial&quot;);\n   NpcTalk(&quot;first you can cook the shrimps on my fire here if you like&quot;);\n   player&#46;setTutorialStatus(45);\n   player&#46;getActionSender()&#46;sendTutorialStatus();\n} else\nif((HasItem(376, 1)) &amp;&amp; (!HasItem(349, 1))) { // The player has a net but hasn''t caught any shrimp yet\n   NpcTalk(&quot;Please come talk to me once you have caught a shrimp&quot;);\n} else {\n   PlayerTalk(&quot;Hi are you here to tell me how to catch fish?&quot;);\n   NpcTalk(&quot;Yes that''s right, you''re a smart one&quot;);\n   NpcTalk(&quot;Fishing is a useful skill&quot;);\n   NpcTalk(&quot;You can sell high level fish for lots of money&quot;);\n   NpcTalk(&quot;Or of course you can cook it and eat it to heal yourself&quot;);\n   NpcTalk(&quot;Unfortunately you''ll have to start off catching shrimps&quot;);\n   NpcTalk(&quot;Till your fishing level gets higher&quot;);\n   NpcTalk(&quot;you''ll need this&quot;);\n   SendMessage(&quot;the fishing instructor gives you a somewhat odd looking net&quot;);\n   AddItem(376, 1); // Add a net to the players inventory\n   Wait(1000);\n   NpcTalk(&quot;Go catch some shrimp&quot;);\n   NpcTalk(&quot;left click on the sparkling piece of water&quot;);\n   NpcTalk(&quot;while you have the net in your inventory you might catch some fish&quot;);\n}[/code:38red54p]\n482 - mining instructor.BSH\n\n[code:38red54p]\n/**\n * 482 - mining instructor @author Yong Min\n */\n\nif(player&#46;getTutorialStatus() == 50) {\n   NpcTalk(&quot;Please proceed through the next door&quot;);\n} else\nif((HasItem(156, 1)) &amp;&amp; (!HasItem(202, 1))) { // The player has been given the pickaxe but not yet obtained an ore\n   NpcTalk(&quot;to mine a rock just left click on it&quot;);\n   NpcTalk(&quot;If you have a pickaxe in your inventory you might get some ore&quot;);\n} else\nif((HasItem(156, 1)) &amp;&amp; (HasItem(202, 1))) { // The player has been given the pickaxe and has obtained the ore\n   NpcTalk(&quot;very good&quot;);\n   NpcTalk(&quot;If at a later date you find a rock with copper ore&quot;);\n   NpcTalk(&quot;You can take the copper ore and tin ore to a furnace&quot;);\n   NpcTalk(&quot;use them on the furnace to make bronze bars&quot;);\n   NpcTalk(&quot;which you can then either sell&quot;);\n   NpcTalk(&quot;or use on anvils with a hammer&quot;);\n   NpcTalk(&quot;To make weapons&quot;);\n   NpcTalk(&quot;as your mining and smithing levels grow&quot;);\n   NpcTalk(&quot;you will be able to mine various exciting new metals&quot;);\n   NpcTalk(&quot;no