(110, 109, 12, 2, 0, '65.190.15.53', 1306033132, 0, 1, 1, 1, 1, '', 'Starting class selection', '[code:2m14o3l8]package org&#46;rscdaemon&#46;server&#46;npchandler;\n\nimport  org&#46;rscdaemon&#46;server&#46;model&#46;*;\nimport  org&#46;rscdaemon&#46;server&#46;util&#46;Formulae;\nimport  org&#46;rscdaemon&#46;server&#46;event&#46;ShortEvent;\n\npublic class PlayerClass  implements NpcHandler {\n        /**\n         * World instance\n          */\n        public static final World world =  World&#46;getWorld();\n\n        public void handleNpc(final Npc npc,  Player player) throws Exception {\n                       player&#46;informOfNpcMessage(new ChatMessage(npc, &quot;Greetings!!&quot;, player));\n                       player&#46;informOfNpcMessage(new ChatMessage(npc, &quot;I  see you are new the this land&quot;, player));\n                       player&#46;informOfNpcMessage(new ChatMessage(npc, &quot;You can become very  powerful with the right help&quot;, player));\n                       player&#46;informOfNpcMessage(new ChatMessage(npc, &quot;Would you like me to  show you the way?&quot;, player));\n                       player&#46;setBusy(true);\n                         world&#46;getDelayedEventHandler()&#46;add(new ShortEvent(player) {\n                         public void action() {\n                         owner&#46;setBusy(false);\n                                String&#91;&#93;  options = new String&#91;&#93;{&quot;I wish to become a mighty warrior&quot;, &quot;I would  like to become a powerful magician&quot;, &quot;I want to be an adventurer&quot;,  &quot;Teach me the ways of the ranger&quot;, &quot;I want to mine like the  Dwarfs!&quot;};//NPC Chat options, you can make more if you want\n                                 owner&#46;setMenuHandler(new MenuHandler(options) {\n                                 public void handleReply(final int option,  final String reply) {\n                                 if(owner&#46;isBusy()) {\n                                                         return;\n                                                }\n                                                  owner&#46;informOfChatMessage(new ChatMessage(owner, reply, npc));\n                                                 owner&#46;setBusy(true);\n                                                  world&#46;getDelayedEventHandler()&#46;add(new ShortEvent(owner) {\n                                                         public void action() {\n                                                                  owner&#46;setBusy(false);\n                                                                 if(option == 0) {\n                                                                                  owner&#46;informOfNpcMessage(new ChatMessage(npc, &quot;A wise choice! You are  now a mighty warrior!&quot;, owner));\n                                                                                 owner&#46;getInventory()&#46;add(new  InvItem(10, 100)); //100gp\n                                                                                 owner&#46;getInventory()&#46;add(new  InvItem(66, 1)); //bronze short sword\n                                                                                  owner&#46;getInventory()&#46;add(new InvItem(4, 1)); //wooden shield\n                                                                                  owner&#46;getActionSender()&#46;sendInventory();        \n                                                                                  owner&#46;setCurStat(0, 5);//(Stat Number,Stat Level)\n                                                                                  owner&#46;setMaxStat(0, 5);//(Stat Number,Stat Level)\n                                                                                  owner&#46;setExp(0, Formulae&#46;lvlToXp(5));//(Stat Number,Stat Level)\n                                                                                  owner&#46;setCurStat(2, 5);//(Stat Number,Stat Level)\n                                                                                  owner&#46;setMaxStat(2, 5);//(Stat Number,Stat Level)\n                                                                                  owner&#46;setExp(2, Formulae&#46;lvlToXp(5));//(Stat Number,Stat Level)\n                                                                                  owner&#46;setCurStat(3, 12);//(Stat Number,Stat Level)\n                                                                                  owner&#46;setMaxStat(3, 12);//(Stat Number,Stat Level)\n                                                                                  owner&#46;setExp(3, Formulae&#46;lvlToXp(12));//(Stat Number,Stat Level)\n                                                                                  owner&#46;getActionSender()&#46;sendMessage(&quot;Good luck on your journey!&quot;);\n                                                                                  //owner&#46;teleport(123, 647, false);\n                                                                                 npc&#46;unblock();                                                \n                                                                 }\n                                                                 if(option == 1) {\n                                                                                  owner&#46;informOfNpcMessage(new ChatMessage(npc, &quot;A wise choice! You are  now a powerful magician!&quot;, owner));\n                                                                                  owner&#46;getInventory()&#46;add(new InvItem(10, 100)); //100gp\n                                                                                  owner&#46;getInventory()&#46;add(new InvItem(199, 1)); //black wizard hat\n                                                                                  owner&#46;getInventory()&#46;add(new InvItem(100, 1)); //