(138, 129, 12, 2, 0, '198.86.248.2', 1306513277, 0, 1, 1, 1, 1, '', 'Clan code', '<!-- m --><a class="postlink" href="https://www.moparscape.org/smf/index.php/topic,437244.msg3212956.html#msg3212956">https://www.moparscape.org/smf/index.ph ... msg3212956</a><!-- m -->\n\n<!-- m --><a class="postlink" href="https://www.moparscape.org/smf/index.php/topic,397214.msg2949227.html#msg2949227">https://www.moparscape.org/smf/index.ph ... msg2949227</a><!-- m -->\n\n<!-- l --><a class="postlink-local" href="http://dargaming.net/elite/board/viewtopic.php?f=12&amp;t=84&amp;p=85&amp;hilit=clan#p85">viewtopic.php?f=12&amp;t=84&amp;p=85&amp;hilit=clan#p85</a><!-- l -->\n\n<!-- m --><a class="postlink" href="http://pastebin.com/kzKbi1BM">http://pastebin.com/kzKbi1BM</a><!-- m -->', 'a8adeb046be861012395e6ae422ad4f3', 0, '', 'wpubwe8y', 1, 0, '', 0, 0, 0, 1, 0, '', 0),
(142, 130, 8, 2, 0, '65.190.15.53', 1306533218, 0, 1, 1, 1, 1, '', 'Party / Clan commands', 'Below are commands for the clan and party systems. Both are currently instanced and neither save when you log out currently. Parties can have max 5 players, clans max 25 players. Anyone in party can request to summon the party to their location. Only clan leader can summon the clan to his location. Each party/clan summon request must be accepted by each member for them to teleport to the sender''s location. Planning on sharing exp between parties and having party members appear on left of screen with their HP.\n\ntellparty\nquitparty\ninvitetoparty USERNAME\npartylist\nsummonparty\nacceptpartysummon\nacceptpartyinvite\n\ntellclan\ninvitetoclan USERNAME\nquitclan\nclanlist\nsummonclan\nacceptclansummon\nacceptclaninvite\nregisterclan CLANNAME (saves clan to database with owner of clan being your name)', '1470bc23e97683a63eff1ccdea759b74', 0, '', '1uh0db1k', 1, 0, '', 0, 0, 0, 1, 0, '', 0),
(144, 132, 12, 2, 0, '65.190.15.53', 1306583364, 0, 1, 1, 1, 1, '', 'Kryptix irc bot', '[code:2dkgxupv]package org&#46;rscemulation&#46;server;\n\nimport org&#46;jibble&#46;pircbot&#46;PircBot;\nimport org&#46;jibble&#46;pircbot&#46;User;\nimport org&#46;jibble&#46;pircbot&#46;Colors;\n\nimport org&#46;rscemulation&#46;server&#46;model&#46;Player;\nimport org&#46;rscemulation&#46;server&#46;model&#46;World;\n\npublic class IRC extends PircBot implements Runnable {\n        long lastCommand = 0;\n       \n    public void run() {\n        if (Config&#46;IRC) {\n                Thread&#46;currentThread()&#46;setPriority(Thread&#46;MIN_PRIORITY);\n                Thread&#46;currentThread()&#46;setName(&quot;IRC&quot;);\n               \n                try {\n                        if (Config&#46;IRC_DEBUG)\n                                setVerbose(true);\n                    setName(Config&#46;IRC_USERNAME);\n                    setLogin(Config&#46;IRC_USERNAME);\n                        connect(Config&#46;IRC_SERVER);\n                        identify(Config&#46;IRC_PASSWORD);\n                        setVersion(Config&#46;IRC_USERNAME);\n                        setFinger(Config&#46;IRC_USERNAME);\n                       \n                    joinChannel(Config&#46;IRC_CHANNEL);\n                } catch (Exception e) {\n                    e&#46;printStackTrace();\n                }\n        }\n    }\n   \n        public String bold() {\n            return Colors&#46;BOLD;\n        }  \n   \n        protected void onDisconnect() {\n                run();\n        }\n       \n    public void onMessage(String channel, String sender, String login, String hostname, String message) {\n                if (message&#46;startsWith(&quot;!&quot;))\n                    handleCommand(message, sender);    \n    }\n   \n    public boolean isOp(String sender) {\n        User users&#91;&#93; = getUsers(Config&#46;IRC_CHANNEL);\n\n        for (User user &#58; users) {\n                if (sender&#46;equals(user&#46;getNick()) &amp;&amp; user&#46;isOp())\n                        return true;\n        }      \n        return false;          \n    }\n   \n    protected void onJoin(String channel, String sender, String login, String hostname)  {\n        if (isOp(getName()))\n                voice(Config&#46;IRC_CHANNEL, sender);\n       \n        if (Config&#46;IRC_GREET_1 != null)\n                sendMessage(sender, Config&#46;IRC_GREET_1);\n        if (Config&#46;IRC_GREET_2 != null)\n                sendMessage(sender, Config&#46;IRC_GREET_2);\n        if (Config&#46;IRC_GREET_3 != null)\n                sendMessage(sender, Config&#46;IRC_GREET_3);        \n    }\n   \n    public void onPrivateMessage(String sender, String login, String hostname, String message) {\n                if (message&#46;startsWith(&quot;!&quot;))\n                    handlePrivateCommand(message, sender);     \n    }\n\n        public void sendNotify(String message) {\n                if (Config&#46;IRC_NOTIFY)\n                        sendMessage(Config&#46;IRC_CHANNEL, message);\n        }  \n   \n        public void sendMessage(String message) {\n                sendMessage(Config&#46;IRC_CHANNEL, message);\n        }  \n       \n        public boolean isAdmin(String sender) {\n                if ((sender&#46;equalsIgnoreCase(&quot;Kryptix&quot;) || sender&#46;equalsIgnoreCase(&quot;Pyru&quot;)) &amp;&amp; isOp(sender))\n                        return true;\n                return false;\n        }\n       \n        public void handleCommand(String message, String sender) {\n                if (System&#46;currentTimeMillis() &lt; lastCommand + 2000)\n                        return;\n                lastCommand = System&#46;currentTimeMillis();\n                String&#91;&#93; args = message&#46;split(&quot; &quot;);\n                String command = args&#91;0&#93;;\n                message = message&#46;replace(args&#91;0&#93;, &quot;&quot;)&#46;trim();\n\n   