(161, 145, 12, 2, 0, '69.134.170.79', 1317690794, 0, 1, 1, 1, 1, '', 'Prayer paralyze monster', 'This goes in the event/FightEvent handler.\n\n[code:1d2k5vfj]package com&#46;rscd&#46;event;\n\nimport com&#46;rscd&#46;DelayedEventHandler;\nimport com&#46;rscd&#46;model&#46;*;\nimport com&#46;rscd&#46;util&#46;Formulae;\nimport com&#46;rscd&#46;entityhandling&#46;defs&#46;extras&#46;ItemDropDef;\nimport com&#46;rscd&#46;entityhandling&#46;defs&#46;extras&#46;DropDef;\nimport com&#46;rscd&#46;entityhandling&#46;EntityHandler;\nimport com&#46;rscd&#46;util&#46;Config;\n\nimport java&#46;util&#46;*;\n\n/**\n * This class handles combat between\n * 2 mobs&#46;\n */\npublic class FightEvent extends DelayedMobEvent \n{\n	private Mob affectedMob; // The ''defender''\n	private Random r = new Random();\n	private int hits;\n	private int damage = 0;\n	private final static int FIGHT_DELAY = 1200;\n	private Mob mobThatWasAttacked = null;\n	long startTime = 0;\n	\n	public final int Rand(int low, int high) \n	{\n       	return low + r&#46;nextInt(high - low);\n    }\n    \n	public FightEvent(Mob owner, Mob affectedMob) \n	{\n		super(owner, FIGHT_DELAY); // Timer is the delay between hits&#46;\n		\n		this&#46;affectedMob = affectedMob;\n		hits = 0;\n		\n		if(owner instanceof Player)\n			owner&#46;setFightEvent(this);\n		if(affectedMob instanceof Player)\n			affectedMob&#46;setFightEvent(this);\n			\n		mobThatWasAttacked = affectedMob;\n		startTime = System&#46;currentTimeMillis();\n		//owner&#46;incCurrentRound();\n	}\n	\n	public void stop() \n	{\n		super&#46;running = false;\n	}\n	\n	public void run() \n	{\n		Mob attacker = null, opponent = null;\n		Player attackerPlayer = null, opponentPlayer = null;\n		\n		if(hits++ % 2 == 0) \n		{\n			attacker = owner;\n			opponent = affectedMob;\n		} else \n		{\n			attacker = affectedMob;\n			opponent = owner;\n		}\n		\n		mobThatWasAttacked&#46;setCurrentRound(((int)(System&#46;currentTimeMillis() - attacker&#46;getCombatTimer()) / FIGHT_DELAY));\n		\n		if(!attacker&#46;equals(mobThatWasAttacked))\n			attacker&#46;setCurrentRound(((int)(System&#46;currentTimeMillis() - attacker&#46;getCombatTimer()) / FIGHT_DELAY) - 1);\n		else\n		if(!opponent&#46;equals(mobThatWasAttacked))\n			opponent&#46;setCurrentRound(((int)(System&#46;currentTimeMillis() - opponent&#46;getCombatTimer()) / FIGHT_DELAY) - 1); \n		\n		if(attacker instanceof Player)\n			attackerPlayer = (Player)attacker;\n		else\n			((Npc)attacker)&#46;updatePosition();\n			\n		if(opponent instanceof Player)\n			opponentPlayer = (Player)opponent;\n			\n		if(attacker instanceof Npc &amp;&amp; opponent instanceof Player)\n		{\n			if(opponentPlayer&#46;isPrayerActivated(PARALYZE_MONSTER))\n				return;\n		}\n\n		damage = Formulae&#46;calcFightHit(attacker, opponent);\n  		opponent&#46;setLastDamage(damage);\n  		\n  		int newHp = opponent&#46;getCurStat(3) - damage;\n  		opponent&#46;setCurStat(3, newHp);\n  		\n  		ArrayList&lt;Player&gt; playersToInform = new ArrayList&lt;Player&gt;();\n  		playersToInform&#46;addAll(opponent&#46;getViewArea()&#46;getPlayersInView());\n  		\n  		for(Player p &#58; playersToInform) \n  		{\n  			if(opponentPlayer != null)  // Opponent = player\n  				p&#46;informOfModifiedHits(opponentPlayer);\n  			if(opponent instanceof Npc) // Opponent = npc\n  				p&#46;informOfModifiedHits((Npc)opponent);\n  		}\n  		\n  		if(opponent instanceof Player) // If the opponent is a player, send their new hits&#46;\n			opponentPlayer&#46;getActionSender()&#46;sendStat(3);\n			\n		String combatSound = damage &gt; 0 ? &quot;combat1b&quot; &#58; &quot;combat1a&quot;;\n		\n		if(attacker instanceof Player) // If the attacker is a player, send the sound&#46;\n			attackerPlayer&#46;getActionSender()&#46;sendSound(combatSound);\n			\n		if(opponent instanceof Player) // If the opponent is a player, send the sound&#46;\n			opponentPlayer&#46;getActionSender()&#46;sendSound(combatSound);\n		\n  		if(newHp &lt;= 0) // Defending Mob dead&#46;\n  		{\n  			/** NPCs in combat *\n  			if(attacker instanceof Npc &amp;&amp; opponent instanceof Npc)\n  			{\n  				Npc attackerNpc = (Npc)attacker;\n  				Npc opponentNpc = (Npc)opponent;\n  				System&#46;out&#46;println(attackerNpc&#46;getDef()&#46;getName() + &quot; killed &quot; + opponentNpc&#46;getDef()&#46;getName() + &quot;!&quot;);\n\n  				World&#46;getWorld()&#46;unregisterNpc((Npc)opponent);\n		  					\n				Collection&lt;Player&gt; allWatched = opponent&#46;getWatchedPlayers()&#46;getAllEntities();\n				System&#46;out&#46;println(&quot;Watched&#58; &quot; + allWatched&#46;size());\n	  			attacker&#46;removeWatchedNpc((Npc)opponent);\n	  			\n	  			for(Player pl &#58; allWatched) \n	  				pl&#46;removeWatchedNpc((Npc)opponent);\n\n				World&#46;getWorld()&#46;registerItem(new Item(20, opponent&#46;getX(), opponent&#46;getY(), 1, true, null));\n  			}*/\n  			\n  			//System&#46;out&#46;println(&quot;Attacker killed defender mob in fight event&#46;&quot;);\n  			if(attackerPlayer != null &amp;&amp; opponentPlayer != null) // Players\n  			{\n  				if(attackerPlayer&#46;isDueling() &amp;&amp; opponentPlayer&#46;isDueling()) // If dueling\n  				{\n  					if(attackerPlayer&#46;getWishToDuel()&#46;equals(opponentPlayer))\n  					{\n  						//System&#46;out&#46;println(attackerPlayer&#46;getUsername() + &quot; killed &quot; + opponentPlayer&#46;getUsername() + &quot; in DUEL&quot;);\n  						opponentPlayer&#46;killedByDuel(attackerPlayer);\n  						attackerPlayer&#46;unsetDuelOptions();\n  					}\n  				} else\n  					opponentPlayer&#46;killedBy(attackerPlayer);\n  			} else\n  			{\n	  			if(opponentPlayer != null)\n	  				opponentPlayer&#46;killedBy(attacker);\n	  			else\n	  			{\n	  				if(opponent instanceof Npc)\n	  				{\n	  					((Npc)opponent)&#46;die(attacker);\n	  				} else\n	  					System&#46;out&#46;println(&quot;Strange entity killed by FightEvent&#46;&quot;);\n	  			}\n  			}\n  			\n  			if(attacker instanceof Player)\n  			{\n	  			int exp = Formulae&#46;roundUp(((opponent&#46;getCombatLevel() * 2) + 10) * 1&#46;5);\n	  			switch(attackerPlayer&#46;getCombatStyle()) \n	  			{\n					case 0&#58;\n						for(int x = 0; x &lt; 3; x++)\n							attackerPlayer&#46;incExp(x, exp / 3);\n							\n						break;\n					case 1&#58;\n						attackerPlayer&#46;incExp(2, exp);\n						break;\n					case 2&#58;\n						attackerPlayer&#46;incExp(0, exp);\n						break;\n					case 3&#58;\n						attackerPlayer&#46;incExp(1, exp);\n						break;\n				}\n				\n	  			attackerPlayer&#46;incExp(3, exp / 3);\n	  			attackerPlayer&#46;getActionSender()&#46;sendStats();\n  			}\n  			\n  			attacker&#46;setBusy(false);\n  			attacker&#46;setSprite(4);\n  			attacker&#46;setOpponent(null);\n  			attacker&#46;setCombatTimer();\n  			attacker&#46;setState(MobState&#46;IDLE);\n  			attacker&#46;resetCurrentRound();\n  			opponent&#46;resetCurrentRound();\n  			\n  			if(attacker instanceof Player)\n  				attackerPlayer&#46;setNeedsUpdate(true);\n  			else\n  			if(opponent instanceof Player)\n  				opponentPlayer&#46;setNeedsUpdate(true);\n  			\n  			super&#46;running = false;\n  		}\n  		\n		damage = 0;\n	}\n\n	public void hitNext(int newHit)\n	{\n		damage = newHit;\n	}\n	\n	\n	public Mob getAffectedMob() \n	{\n		return affectedMob;\n	}\n	\n	public boolean equals(Object o) \n	{\n		if(o instanceof FightEvent) \n		{\n			FightEvent e = (FightEvent)o;\n			return e&#46;belongsTo(owner) &amp;&amp; e&#46;getAffectedMob()&#46;equals(affectedMob);\n		}\n		\n		return false;\n	}\n}[/code:1d2k5vfj]', '80ee326eaf8669eafad8d7a34e4b6331', 0, 'AIA=', '1d2k5vfj', 1, 0, '', 0, 0, 0, 1, 0, '', 0),
(162, 146, 12, 2, 0, '69.134.170.79', 1317691021, 0, 1, 1, 1, 1, '', 'Login server password protection', 'Use this if you have your login server bound to a publicly connectable port. It will prevent rogue world servers from connecting and ruining your database.\n\n\nopen LS.conf, add this to it\n\n[code:27tnh9t5]&lt;entry key=&quot;lspass&quot;&gt;password&lt;/entry&gt;[/code:27tnh9t5]\n\nNow open world.xml and add this to it\n\n[code:27tnh9t5]&lt;entry key=&quot;lspass&quot;&gt;password&lt;/entry&gt;[/code:27tnh9t5]\n\nReplace password with the password you want\n\nnow go to src/org/rscdaemon/server/util/Config.java and find\n\n[code:27tnh9t5]LS_PORT = Integer&#46;parseInt(props&#46;getProperty(&quot;lsport&quot;));[/code:27tnh9t5]\n\nunder that put\n\n[code:27tnh9t5]LS_PASSWORD = props&#46;getProperty(&quot;lspass&quot;);[/code:27tnh9t5]\n\nnow find\n\n[code:27tnh9t5]public static String SERVER_IP, SERVER_NAME, RSCD_HOME, CONF_DIR, SERVER_LOCATION, LS_IP;[/code:27tnh9t5]\n\nreplace that with\n\n[code:27tnh9t5]public static String SERVER_IP, SERVER_NAME, RSCD_HOME, CONF_DIR, SERVER_LOCATION, LS_IP, LS_PASSWORD;[/code:27tnh9t5]\n\nk save and close that\n\nnow go to /src/org/rscdaemon/server/packetbuilder/loginserver/MiscPacketBuilder.java\n\nfind \n\n[code:27tnh9t5]s&#46;addShort(Config&#46;SERVER_NUM);[/code:27tnh9t5]\n\nunder that put\n\n[code:27tnh9t5]s&#46;addBytes(Config&#46;LS_PASSWORD&#46;getBytes());[/code:27tnh9t5]\n\nsave and close that. Now go to /src/org/rscdaemon/ls/util/Config.java and find \n\n[code:27tnh9t5]LS_PORT = Integer&#46;parseInt(props&#46;getProperty(&quot;lsport&quot;));[/code:27tnh9t5]\n\nand under that put\n\n[code:27tnh9t5]LS_PASSWORD = props&#46;getProperty(&quot;lspass&quot;);[/code:27tnh9t5]\n\nthen find \n\n[code:27tnh9t5]public static String RSCDLS_HOME, CONF_DIR, LOG_DIR, MYSQL_HOST, MYSQL_DB, MYSQL_USER, MYSQL_PASS, LS_IP, QUERY_IP;[/code:27tnh9t5]\n\nand replace it with\n\n[code:27tnh9t5]public static String RSCDLS_HOME, CONF_DIR, LOG_DIR, MYSQL_HOST, MYSQL_DB, MYSQL_USER, MYSQL_PASS, LS_IP, QUERY_IP, LS_PASSWORD;[/code:27tnh9t5]\n\nsave and close that. \n\nNow go to /src/org/rscdaemon/ls/packethandler/loginserver/RegisterWorld.java and find\n\n[code:27tnh9t5]int id = p&#46;readShort();[/code:27tnh9t5]\n\nthen underneath that put \n\n[code:27tnh9t5]String passrecv = p&#46;readString()&#46;trim();[/code:27tnh9t5]\n\nthen find \n\n[code:27tnh9t5]if(server&#46;getWorld(id) == null) {[/code:27tnh9t5]\n\nreplace it with\n\n[code:27tnh9t5]if(server&#46;getWorld(id) == null&amp;&amp;passrecv!=null) {[/code:27tnh9t5]\n\nthen underneath that put\n\n[code:27tnh9t5]if(!passrecv&#46;equals(Config&#46;LS_PASSWORD)){\nSystem&#46;out&#46;println(&quot;Failed to authenticate world &quot;+id);\nreturn;\n}[/code:27tnh9t5]\n\nclose, save, compile, should be working.\nHave fun with your secured login server!!!', '44a2329a784f60d86f65e2cc545424cf', 0, 'AIA=', '27tnh9t5', 1, 0, '', 0, 0, 0, 1, 0, '', 0),
(163, 147, 12, 2, 0, '69.134.170.79', 1317693466, 0, 1, 1, 1, 1, '', 'Points System', 'First declare this in Player.java\n\n[code:4yyf2o2h]public int points = 0;[/code:4yyf2o2h]\n\nand these\n\n[code:4yyf2o2h]public int getPoints()\n	{\n		return points;\n	}\n	public int incPoints()\n	{\n		return points++;\n	}[/code:4yyf2o2h]\n\nReplace this (or what resembles this) in FightEvent.java\n\n[code:4yyf2o2h]if(newHp &lt;= 0) {\n  			opponent&#46;killedBy(attacker, false);\n  			if(attacker instanceof Player) {\n  				Player attackerPlayer = (Player)attacker;\n	      			int exp = DataConversions&#46;roundUp(Formulae&#46;combatExperience(opponent) / 4D);\n	      			switch(attackerPlayer&#46;getCombatStyle()) {\n					case 0&#58;\n						for(int x = 0;x &lt; 3;x++) {\n							attackerPlayer&#46;incExp(x, exp, true);\n							attackerPlayer&#46;getActionSender()&#46;sendStat(x);\n						}\n						break;\n					case 1&#58;\n						attackerPlayer&#46;incExp(2, exp * 3, true);\n						attackerPlayer&#46;getActionSender()&#46;sendStat(2);\n						break;\n					case 2&#58;\n						attackerPlayer&#46;incExp(0, exp * 3, true);\n						attackerPlayer&#46;getActionSender()&#46;sendStat(0);\n						break;\n					case 3&#58;\n						attackerPlayer&#46;incExp(1, exp * 3, true);\n						attackerPlayer&#46;getActionSender()&#46;sendStat(1);\n						break;\n				}\n	      			attackerPlayer&#46;incExp(3, exp, true);\n	      			attackerPlayer&#46;getActionSender()&#46;sendStat(3);\n  			}\n  			attacker&#46;resetCombat(CombatState&#46;WON);\n  			opponent&#46;resetCombat(CombatState&#46;LOST);\n  		}\n	}[/code:4yyf2o2h]\n\nwith this\n\n[code:4yyf2o2h]if(attacker instanceof Player)\n  			{\n			int points = attackerPlayer&#46;getPoints() + (opponent&#46;getCombatLevel() * 2);\n			Party party = new Party(attackerPlayer);\n			party&#46;shareExp(opponent&#46;getCombatLevel() * 2, attackerPlayer&#46;getUsername());\n  			attackerPlayer&#46;setPoints(points);\n	  		attackerPlayer&#46;getActionSender()&#46;sendStats();\n  			}\n  			attacker&#46;setBusy(false);\n  			attacker&#46;setSprite(4);\n  			attacker&#46;setOpponent(null);\n  			attacker&#46;setCombatTimer();\n  			attacker&#46;setState(MobState&#46;IDLE);\n  			attacker&#46;resetCurrentRound();\n  			opponent&#46;resetCurrentRound();\n  			\n  			if(attacker instanceof Player)\n  				attackerPlayer&#46;setNeedsUpdate(true);\n  			else\n  			if(opponent instanceof Player)\n  				opponentPlayer&#46;setNeedsUpdate(true);\n  			\n  			super&#46;running = false;\n  		}\n  		\n		damage = 0;\n	}[/code:4yyf2o2h]\n\nAdd this in PlayerLoader.java\n\np.setPoints(result.getInt(&quot;points&quot;));\n\nAnd your done. Thats your working point system. The code for the Point Seller is:\n\n[code:4yyf2o2h]package com&#46;rscd&#46;npchandler;\n\nimport com&#46;rscd&#46;packetbuilder&#46;StaticPacketBuilder;\nimport com&#46;rscd&#46;model&#46;*;\nimport com&#46;rscd&#46;event&#46;ShortEvent;\nimport com&#46;rscd&#46;util&#46;*;\n\nimport com&#46;rscd&#46;event&#46;DelayedOptionsMenu;\n\npublic class StatsPoints extends NpcHandlerDef implements NpcHandler \n{\n	public World world = World&#46;getWorld();\n	private final String&#91;&#93; mainmenu = new String&#91;&#93;\n	{\n		&quot;Attack&quot;,\n		&quot;Strength&quot;,\n		&quot;Defense&quot;,\n		&quot;Magic&quot;,\n		&quot;Ranged&quot;,\n		\n	};\n	private final String&#91;&#93; mainmenu2 = new String&#91;&#93;\n	{\n		&quot;2&#46;5k 25pts&quot;,\n		&quot;50k 500pts&quot;,\n		&quot;100k 1000pts&quot;,\n		&quot;1000k 10000pts&quot;,\n		&quot;Take all&quot;,\n	};\n	public void handleNpc(final Npc affectedNpc, final Player player) {\n				sayNpcMessage(&quot;Hello,i can exchange your points to experience&#46;&quot;, affectedNpc, player);\n				world&#46;getDelayedEventHandler()&#46;add(new DelayedOptionsMenu(player, mainmenu, 1200) {\n					public void action() \n					{\n						owner&#46;setMenuHandler(new MenuHandler(menu)\n						{\n							public void handleReply(final int option, String response)\n	