                                          owner&#46;getActionSender()&#46;sendStat(10);\n                                                }\n                                                else {\n                                                        owner&#46;getActionSender()&#46;sendMessage(&quot;You fail to catch anything&#46;&quot;);\n                                                }\n                                                if(owner&#46;getInventory()&#46;full())\n                                                        {\n                                                        owner&#46;getActionSender()&#46;sendMessage(&quot;Your inventory is full&#46;&quot;);\n                                                        }\n                                                else\n                                                        {\n                                                        handleFishing(click);\n                                                        owner&#46;setBusy(true);\n                                                        }\n                                                owner&#46;setBusy(false);\n                                        }[/code:3oyxdpbe]', 'ab2e3772299376bcf657d4de592db91c', 0, 'AIA=', '3oyxdpbe', 1, 0, '', 0, 0, 0, 1, 0, '', 0),
(175, 155, 12, 2, 0, '69.134.170.79', 1318298815, 0, 1, 1, 1, 1, '', 'GetClassName+Printing', 'Add this in SessionRequest class.\n\n[code:202kwk0v]Logger&#46;print(player&#46;getCurrentIP() + &quot; - &quot; + player&#46;getClassName());[/code:202kwk0v]\n\nPrints the user''s clients Main class, along with their IP. Helps spots bots.', 'f560f594b17890572aeb5c6eec04a3d1', 0, 'AIA=', '202kwk0v', 1, 0, '', 0, 0, 0, 1, 0, '', 0);
